page.code:
app.engine('pdfxml', require('express-hogan-pdf'));
app.set('views', './views');
app.set('view engine', 'pdfxml');

app.get('/pdf', (req, res, next) => {
    res.locals.key = 'value';
    res.render('template.pdf.xml');
});

const hoganPdf = require('express-hogan-pdf');

const locals = { key: 'value' };

hoganPdf.fileToStream('source.pdf.xml', locals, (err, stream) => {
});

hoganPdf.fileToBuffer('source.pdf.xml', locals, (err, buffer) => {
});

hoganPdf.fileToFile('source.pdf.xml', locals, 'dest.pdf', (err) => {
});
